package ajithv;

public class assignment_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=25;
		int b;
		b=a;
		System.out.println(b);
		b+=a;
		System.out.println(b);
		b-=a;
		System.out.println(b);
		b*=a;
		System.out.println(b);
		b/=a;
		System.out.println(b);

	}

}
